mapper.update:
UPDATE "BOARD" SET
BOARD_TITLE = #{boardTitle},
BOARD_CONTENT = #{boardContent}
WHERE BOARD_CODE = #{boardCode}
AND BOARD_NO = #{boardNo}
AND MEMBER_NO = #{memberNo}

UPDATE "BOARD" SET
BOARD_DEL_FL = 'Y'
WHERE BOARD_CODE = #{boardCode}
AND BOARD_NO = #{boardNo}
AND MEMBER_NO = #{memberNo}

UPDATE "ANNOUNCEMENT" SET
ANNOUNCE_DEL_FL = 'Y'
WHERE ANNOUNCE_NO = #{announceNo}
AND MEMBER_NO = #{memberNo}

UPDATE "ANNOUNCEMENT" SET
ANNOUNCE_NAME = #{announceName},
ANNOUNCE_CONTENT = #{announceContent}
WHERE ANNOUNCE_NO = #{announceNo}
AND MEMBER_NO = #{memberNo}

mapper.insert:
SELECT SEQ_ANNOUNCEMENT_NO.NEXTVAL FROM DUAL
INSERT INTO "ANNOUNCEMENT" VALUES(#{announceNo}, #{announceName},DEFAULT, #{announceContent}, DEFAULT, DEFAULT, #{memberNo}, NULL
)
