mapper.select:
SELECT COUNT(*)
FROM "MEMBER"
WHERE MEMBER_NAME = #{memberName}
AND MEMBER_TEL = #{memberTel}
AND MEMBER_DEL_FL = 'N'

SELECT MEMBER_EMAIL
FROM "MEMBER"
WHERE MEMBER_NAME = #{memberName}
AND MEMBER_TEL = #{memberTel}
AND MEMBER_DEL_FL = 'N'

SELECT COUNT(*)
FROM "MEMBER"
WHERE MEMBER_NAME = #{memberName}
AND MEMBER_EMAIL = #{memberEmail}
AND MEMBER_DEL_FL = 'N'

SELECT MEMBER_EMAIL
FROM "MEMBER"
WHERE MEMBER_NICKNAME = #{memberNickname}
AND MEMBER_TEL = #{memberTel}
AND MEMBER_DEL_FL='N'
