mapper.select:
SELECT MEMBER_MAJOR, MEMBER_INTRODUCE, PROFILE_IMG
FROM MEMBER
WHERE MEMBER_NO = #{memberNo}

SELECT COUNT(*)
FROM (
SELECT STUDY_BOARD_NO FROM STUDY_BOARD WHERE MEMBER_NO = #{memberNo}
UNION ALL
SELECT BOARD_NO FROM BOARD WHERE MEMBER_NO = #{memberNo}
UNION ALL
SELECT HIRE_NO FROM HIRE_INFO WHERE MEMBER_NO = #{memberNo}
)

SELECT COUNT(*) FROM ( SELECT HIRE_NO FROM HIRE_INFO h JOIN HIRE_COMMENT hc USING(HIRE_NO) WHERE hc.MEMBER_NO = #{memberNo}
UNION ALL
SELECT STUDY_BOARD_NO FROM STUDY_BOARD s JOIN STUDY_COMMENT sc USING(STUDY_BOARD_NO) WHERE sc.MEMBER_NO = #{memberNo}
UNION ALL
SELECT BOARD_NO FROM BOARD b JOIN "COMMENT" c USING(BOARD_NO) WHERE c.MEMBER_NO = #{memberNo} )

SELECT HIRE_NO AS "boardNo", HIRE_TITLE AS "boardTitle", HIRE_COMMENT_CONTENT AS "commentContent", TO_CHAR(HIRE_COMMENT_DATE, 'YYYY-MM-DD') AS "commentDate", 3 AS "boardCode" FROM HIRE_INFO h JOIN HIRE_COMMENT USING(HIRE_NO) WHERE h.MEMBER_NO = #{memberNo}
UNION
SELECT STUDY_BOARD_NO, STUDY_BOARD_TITLE, STUDY_COMMENT_CONTENT, TO_CHAR(STUDY_COMMENT_DATE, 'YYYY-MM-DD'), 2 AS "boardCode" FROM STUDY_BOARD s JOIN STUDY_COMMENT USING(STUDY_BOARD_NO) WHERE s.MEMBER_NO = #{memberNo}
UNION
SELECT BOARD_NO, BOARD_TITLE, COMMENT_CONTENT, TO_CHAR(COMMENT_WRITTEN_DATE, 'YYYY-MM-DD'), 1 AS "boardCode" FROM BOARD b JOIN "COMMENT" USING(BOARD_NO) WHERE b.MEMBER_NO = #{memberNo}
