mapper.select:
SELECT MEMBER_MAJOR, MEMBER_INTRODUCE, PROFILE_IMG
FROM MEMBER
WHERE MEMBER_NO = #{memberNo}

SELECT COUNT(*)
FROM (
SELECT STUDY_BOARD_NO FROM STUDY_BOARD WHERE MEMBER_NO = #{memberNo}
UNION ALL
SELECT BOARD_NO FROM BOARD WHERE MEMBER_NO = #{memberNo}
UNION ALL
SELECT HIRE_NO FROM HIRE_INFO WHERE MEMBER_NO = #{memberNo}
)
