mapper.select:
SELECT RULE_CONTENT
FROM STUDY_RULE
WHERE STUDY_NO=#{studyNo}

mapper.update:
UPDATE STUDY 
SET STUDY_NAME = #{studyName},
    STUDY_MAX_COUNT = #{studyMaxCount},
    STUDY_TYPE = #{studyType},
    STUDY_PERIOD = #{studyPeriod}

    , STUDY_MAIN_IMG = #{studyMainImg}

WHERE STUDY_NO = #{studyNo}

mapper.delete:
DELETE FROM STUDY_MEMBER 
WHERE STUDY_NO = #{studyNo} 
AND MEMBER_NO = #{memberNo}

mapper.update:
UPDATE STUDY 
SET STUDY_DEL_FL = 'Y'
WHERE STUDY_NO = #{studyNo}

mapper.select:
SELECT STUDY_CAP
FROM STUDY_MEMBER
WHERE MEMBER_NO = #{memberNo}

SELECT * FROM (
    SELECT SB.STUDY_BOARD_NO, SB.STUDY_BOARD_TITLE, SB.STUDY_BOARD_CONTENT,
           TO_CHAR(SB.STUDY_BOARD_WRITE_DATE, 'YYYY-MM-DD') AS STUDY_BOARD_WRITE_DATE,
           SB.READ_COUNT,
           M.MEMBER_NICKNAME,
           ROW_NUMBER() OVER (ORDER BY SB.STUDY_BOARD_WRITE_DATE DESC) AS RN
    FROM STUDY_BOARD SB
    JOIN MEMBER M ON SB.MEMBER_NO = M.MEMBER_NO
    WHERE SB.STUDY_NO = #{studyNo} 
    AND SB.MEMBER_NO = #{memberNo}
    AND SB.STUDY_BOARD_DEL_FL = 'N'
) WHERE RN > #{offset} AND RN <= #{offset} + #{limit}

SELECT * FROM (
    SELECT SC.STUDY_COMMENT_NO, SC.STUDY_COMMENT_CONTENT, 
           TO_CHAR(SC.STUDY_COMMENT_DATE, 'YYYY-MM-DD') AS STUDY_COMMENT_DATE,
           SB.STUDY_BOARD_TITLE,
           M.MEMBER_NICKNAME,
           ROW_NUMBER() OVER (ORDER BY SC.STUDY_COMMENT_DATE DESC) AS RN
    FROM STUDY_COMMENT SC
    JOIN STUDY_BOARD SB ON SC.STUDY_BOARD_NO = SB.STUDY_BOARD_NO
    JOIN MEMBER M ON SC.MEMBER_NO = M.MEMBER_NO
    WHERE SB.STUDY_NO = #{studyNo}
    AND SC.MEMBER_NO = #{memberNo}
    AND SC.STUDY_COMMENT_DEL_FL = 'N'
) WHERE RN > #{offset} AND RN <= #{offset} + #{limit}

SELECT COUNT(*)
FROM STUDY_MEMBER
WHERE STUDY_NO = #{studyNo}
